// replaced original implementation in order to support cross domain cors requests
// in IE8
forcetk.Client.prototype.ajax = function(path, callback, error, method, payload, retry) {
    var that = this;
    var url = this.instanceUrl + '/services/data' + path;

    return $j.ajax({
        type: method || "GET",
        async: this.asyncAjax,
        url: (this.proxyUrl !== null) ? (this.proxyUrl + '?SalesforceProxy-Endpoint=' + escape(url) + '&' + that.authzHeader + '=' + escape("OAuth " + that.sessionId)): url,
        //url: (this.proxyUrl !== null) ? this.proxyUrl : url,
        contentType: method == "DELETE"  ? null : 'application/json',
        cache: false,
        processData: false,
        data: payload,
        success: callback,
        error: (!this.refreshToken || retry ) ? error : function(jqXHR, textStatus, errorThrown) {
            if (jqXHR.status === 401) {
                that.refreshAccessToken(function(oauthResponse) {
                    that.setSessionToken(oauthResponse.access_token, null,
                    oauthResponse.instance_url);
                    that.ajax(path, callback, error, method, payload, true);
                },
                error);
            } else {
                error(jqXHR, textStatus, errorThrown);
            }
        },
        dataType: "json",
        beforeSend: function(xhr) {
            if (that.proxyUrl !== null) {
                xhr.setRequestHeader('SalesforceProxy-Endpoint', url);
            }
            xhr.setRequestHeader(that.authzHeader, "OAuth " + that.sessionId);
            xhr.setRequestHeader('X-User-Agent', 'salesforce-toolkit-rest-javascript/' + that.apiVersion);
        }
    });
}

// fetches the current user's userid
// leverages the resources to get the users identify URI and
// parse the userid from it
// e.g. identity URI: https://test.salesforce.com/id/00DZ000000N9pRiMAJ/005Z0000001Z905IAC
// orgId: 00DZ000000N9pRiMAJ
// userid: 005Z0000001Z905IAC
forcetk.Client.prototype.userid = function(callback, error) {
    return this.resources(function(result) {
        var identityURI = result['identity'];
        var userid = identityURI.substring(identityURI.lastIndexOf('/') + 1);
        callback(userid);     
    }, error);
}

// fetches all results even if >2000 records returned from query
forcetk.Client.prototype.queryAll = function(soql, callback, error) {
    var that = this;

    // first level call if previous result not on callback
    if (!callback.result) {

        return that.query(soql, function(result) {
            if (result.done) {
                callback(result);
            } else {
                callback.result = result;
                that.queryAll(soql, callback, error);
            }
        }, error);

        
    } else {

        return that.queryMore(callback.result.nextRecordsUrl, function(result) {
            callback.result.records = callback.result.records.concat(result.records);
            if (result.done) {
                callback(callback.result);
            } else {
                callback.result.nextRecordsUrl = result.nextRecordsUrl;
                that.queryAll(soql, callback, error);
            }
            
        }, error );
        
    }         
}

/*

 execute multiple queries in parallel
 queries is an object with "name": "soql" pairs

e.g.
var queries = {
  'territories': 'select Id, Name, Description, ParentTerritoryId from Territory where Id IN (select TerritoryId from UserTerritory where IsActive = true)',
  'userTerritories': 'select Id, IsActive, TerritoryId, UserId from UserTerritory',
  'users': 'select Id, Name, Username from User'
};
*/
forcetk.Client.prototype.queries = function(queries, callback, error) {
    var that = this;
    var fnMap = {};

    _.forEach(queries, function(soql, name) {
      fnMap[name] = (function(soql) {

        return function(cb) {
          that.queryAll(soql, function(result) {
            cb(null, result);
          }, function(err) {
            cb(err);
          });
        }

      })(soql);
    });

    async.parallel(fnMap, function(err, results) {
      if (err) { error(err); }
      if (!err) { callback(results); }
    });

}

forcetk.Client.prototype.removeExtraValues = function(objtype, fields, callback, error) {
    var that = this;

    var standardFields = 'Id,IsLocked,LastReferencedDate,LastModifiedById,LastModifiedDate,LastViewedDate,SystemModstamp,MayEdit,CreatedById,CreatedDate,IsDeleted'.split(',');

    that.describe(objtype, function(result) {
        console.log('removeExtraValues result', result);
        var obj = {};
        _.each(result.fields, function(f) {
            // copy non-null fields
            if (_.has(fields, f.name)) {
                obj[f.name] = fields[f.name];
            }
        });

        // delete any fields that can't be create or updated
        _.each(standardFields, function(fieldName) {
            delete obj[fieldName];
        });

        console.log('removeExtraValues result obj', obj);
        callback(obj);
    }, error);
}

forcetk.Client.prototype.createByRemovingExtraValues = function(objtype, fields, callback, error) {
    var that = this;
    that.removeExtraValues(objtype, fields, function(result) {
        that.create(objtype, result, callback, error);
    }, error);
}

forcetk.Client.prototype.updateByRemovingExtraValues = function(objtype, id, fields, callback, error) {
    var that = this;
    that.removeExtraValues(objtype, fields, function(result) {
        that.update(objtype, id, result, callback, error);
    }, error);

}


