forcetk.Client.prototype.getChildTerritories = function(territoryId, callback, error) {
    var that = this;

    var cacheKey = 'getChildTerritories'  + '(' + territoryId + ')';
    var cachedResult = that.cacheGet(cacheKey);
    if (cachedResult) {
        setTimeout(function() { callback(cachedResult); }, 1);
        return;
    }

    var territoryIdList = [];
    var territoryIdToProcessList = [territoryId];
    
    
    async.whilst(
        
        function () {
            return  (territoryIdToProcessList.length > 0);
        },
        
        function (callback) {
            
            var fns = [];
            for (var i = 0; i < territoryIdToProcessList.length; i++) {
                var territory = territoryIdToProcessList[i];
                fns.push(
                
                    (function(territory){
                
                        return function(cb) {

                            var soql = 'select Id, ParentTerritoryId from Territory where ParentTerritoryId =\'' + territory + '\'';
                            
                            that.query(soql, function(result) {
                               cb(null, result) 
                            }, function(err) {
                                cb(err);
                            });
                        };
                        
                    })(territory)
                                
                );
            }
            
            // clear list
            territoryIdToProcessList = [];
            
            async.parallel(fns, function(err, results) {
                
                if (err) {
                    error(err);
                }
                
                if (!err) {
                    for (var i = 0; i < results.length; i++) {
                        var result = results[i];
                        for (var j = 0; j < result.records.length; j++) {
                            var r = result.records[j];
                            if (territoryIdList.indexOf(r.Id) == -1) {
                                territoryIdList.push(r.Id);
                                territoryIdToProcessList.push(r.Id);                        
                            }
                        }
                    }
                    callback();
                }
                
            });
                        
        },
        function (err) {
            that.cacheSet(cacheKey, territoryIdList);
            callback(territoryIdList);
        }
    );    

};

forcetk.Client.prototype.userTerritories = function(userid, callback, error) {

    var that = this;
    var userTerritoryIds = [];
    
    var cacheKey = 'userTerritories'  + '(' + userid + ')';
    var cachedResult = that.cacheGet(cacheKey);
    if (cachedResult) {
        setTimeout(function() { callback(cachedResult); }, 1);
        return;
    }
    
    async.waterfall([
                
        function(cb) {
            var soql = 'SELECT TerritoryId from UserTerritory where UserId = \'' + userid + '\'';
            that.query(soql, function(result) {
                cb(null, result.records);
            }, function(err) {
               cb(err); 
            });
        },
        
        function(territoryRecords, cb) {
            var fns = [];
            for (var i = 0; i < territoryRecords.length; i++) {
                var territoryId = territoryRecords[i].TerritoryId;
                userTerritoryIds.push(territoryId);
                
                fns.push(
                
                    (function(territoryId){
                
                        return function(cb) {
                            that.getChildTerritories(territoryId, function(result) {
                                cb(null, result);  
                            }, function(err) {
                                cb(err);
                            });
                        };
                        
                    })(territoryId)
                );
            }
            
            async.parallel(fns, function(err, results) {
                
                if (err) {
                    error(err);
                }
                
                if (!err) {
                    
                    for (var i = 0; i < results.length; i++) {
                        userTerritoryIds = userTerritoryIds.concat(results[i]);
                    }
                    cb(null, userTerritoryIds);
                }
                
            });
            
            
        }
        
    ], function(err, result) {
        if (err) {
            error(err);
        }
        
        if (!err) {
            that.cacheSet(cacheKey, result);
            callback(result);
        }
    });

};

